section Section1;
shared DataSetWrangling = let
  AdfDoc = AzureStorage.BlobContents("https://myfirststoragesuresh.blob.core.windows.net/myfirstcontainer/Input/Customer Master.txt"),
  Csv = Csv.Document(AdfDoc, [Delimiter = ",", Encoding = TextEncoding.Utf8, QuoteStyle = QuoteStyle.Csv])
in
  Csv;
shared UserQuery = let
  Source = DataSetWrangling
in
  Source;
